package main

import (
	"fmt"
)

type node struct {
	value interface{}
	next  *node
}

type Queue struct {
	head *node
	tail *node
	size int
}

type pos struct {
	x    int
	y    int
	step int
}

func (q *Queue) Pop() interface{} {
	if q.head == nil {
		panic("empty queue")
	}

	var ans *node = q.head

	q.head = q.head.next
	q.size--

	if q.head == nil {
		q.tail = nil
	}

	return ans.value
}
func (q *Queue) Push(value interface{}) {
	var new_node *node = new(node)
	new_node.value = value
	new_node.next = nil

	if q.tail == nil {
		q.head = new_node
		q.tail = new_node
	} else {
		q.tail.next = new_node
		q.tail = new_node
	}
	q.size++
}

func shortestBridge(A [][]int) int {

	// mark one island
	var q *Queue = new(Queue)
	var i, j, ans int
	var p, t *pos = new(pos), new(pos)
	var flag bool = false

	for i = 0; i < len(A); i++ {
		for j = 0; j < len(A[0]); j++ {
			if A[i][j] != 0 {
				p.x = i
				p.y = j
				A[i][j] = 2
				q.Push(*p)
				flag = true
				break
			}
		}
		if flag {
			break
		}
	}

	for {
		if q.size == 0 {
			break
		}
		*p = q.Pop().(pos)

		// top
		if p.y > 0 && A[p.x][p.y-1] == 1 {
			t.x = p.x
			t.y = p.y - 1
			A[t.x][t.y] = 2
			q.Push(*t)
		}

		// down
		if p.y < len(A)-1 && A[p.x][p.y+1] == 1 {
			t.x = p.x
			t.y = p.y + 1
			A[t.x][t.y] = 2
			q.Push(*t)
		}

		// left
		if p.x > 0 && A[p.x-1][p.y] == 1 {
			t.x = p.x - 1
			t.y = p.y
			A[t.x][t.y] = 2
			q.Push(*t)
		}

		// right
		if p.x < len(A[0])-1 && A[p.x+1][p.y] == 1 {
			t.x = p.x + 1
			t.y = p.y
			A[t.x][t.y] = 2
			q.Push(*t)
		}
	}

	// put island which marked as 2 into queue
	for i = 0; i < len(A); i++ {
		for j = 0; j < len(A[0]); j++ {
			if A[i][j] == 2 {
				p.x = i
				p.y = j
				p.step = 0
				q.Push(*p)
			}
		}
	}

	ans = 0
	for {
		if q.size == 0 {
			break
		}

		*p = q.Pop().(pos)

		// if next to 1, return
		if (p.y > 0 && A[p.x][p.y-1] == 1) || (p.y < len(A)-1 && A[p.x][p.y+1] == 1) || (p.x > 0 && A[p.x-1][p.y] == 1) || (p.x < len(A[0])-1 && A[p.x+1][p.y] == 1) {
			ans = p.step
			break
		}

		// put 0 into queue

		if p.y > 0 && A[p.x][p.y-1] == 0 {
			t.x = p.x
			t.y = p.y - 1
			t.step = p.step + 1
			A[t.x][t.y] = 3
			q.Push(*t)
		}

		if p.y < len(A)-1 && A[p.x][p.y+1] == 0 {
			t.x = p.x
			t.y = p.y + 1
			t.step = p.step + 1
			A[t.x][t.y] = 3
			q.Push(*t)
		}

		if p.x > 0 && A[p.x-1][p.y] == 1 {
			t.x = p.x - 1
			t.y = p.y
			t.step = p.step + 1
			A[t.x][t.y] = 3
			q.Push(*t)
		}

		if p.x < len(A[0])-1 && A[p.x+1][p.y] == 0 {
			t.x = p.x + 1
			t.y = p.y
			t.step = p.step + 1
			A[t.x][t.y] = 3
			q.Push(*t)
		}
	}

	return ans
}

